const request     = require('supertest');
const expect      = require('chai').expect;
const server      = require('./lib/express');
const { token } = require('../lib/env')

describe('Create Asset', function () {

  it('base', function(done) {
    request(server)
      .post('/')
      .send({
        event: 'MODULE_EXEC',
        payload: {
          moduleId: 'create_asset',
          moduleParam: {
            name: undefined,
            description: undefined,
            attachments: undefined,
            primary_attachment: undefined,
            section_id: undefined,
            font_color: undefined,
            background_color: undefined,
            h_size: undefined,
            v_size: undefined,
            placeholder_text: undefined
          },
          registrationData: {
            token
          }
        }
      })
      .set('X_CONVERSE_APP_TOKEN', require('../app-token'))
      .expect(200)
      .end(function(err, res) {
        expect(res.body).to.have.property('status').to.equal(0);
        expect(res.body).to.have.property('value');
        done();
      });
  })

});
